sys_script_include.script:
var CloneAnalyseUtils = Class.create();
CloneAnalyseUtils.prototype = {
    initialize: function() {},

    createRecord: function(response, configuration, source) {
        try {
            var i = 0;
            var responseParsed = JSON.parse(response);
            while (i < responseParsed.result.length) {
                if (source == "Retrieved")
                    var usAlreayCommited = this.checkUS(responseParsed.result[i].name, configuration);

                if (!usAlreayCommited) {
                    var grUpdateSet = new GlideRecord('x_322488_clone_a_0_update_set'); {
                        grUpdateSet.initialize();
                        grUpdateSet.configuration = configuration.sys_id;
                        grUpdateSet.name = responseParsed.result[i].name;
                        grUpdateSet.state = responseParsed.result[i].state;
                        grUpdateSet.instance = responseParsed.result[i].update_source;
                        grUpdateSet.source = source;
                        grUpdateSet.insert();
                    }
                }
                i++;
            }
        } catch (e) {
            gs.info("Clone Analysis :: createRecord : " + JSON.stringify(e), "CloneAnalyseUtils");
        }
    },

    compareRecords: function(response, configuration) {
        try {
            var i = 0;
            var responseParsed = JSON.parse(response);
            while (i < responseParsed.result.length) {
                var grSysUpdateSet = new GlideRecord("x_322488_clone_a_0_update_set");
                grSysUpdateSet.addEncodedQuery("configuration=" + configuration.sys_id + "^name=" + responseParsed.result[i].name + "^source=local");
                grSysUpdateSet.query();
                if (grSysUpdateSet.next()) {
                    grSysUpdateSet.committed_in_the_instance = true;
                    grSysUpdateSet.update();
                } else {
                    //var grSysUpdateSet = new GlideRecord("x_322488_clone_a_0_update_set");
                    grSysUpdateSet.addEncodedQuery("configuration=" + configuration.sys_id + "^name=" + responseParsed.result[i].name + "^source=Retrieved");
                    grSysUpdateSet.query();
                    if (grSysUpdateSet.next()) {
                        grSysUpdateSet.retrieved_in_the_instance = true;
                        grSysUpdateSet.update();

                    }
                }
                i++;
            }
        } catch (e) {
            gs.info("Clone Analysis :: compareRecords : " + JSON.stringify(e), "CloneAnalyseUtils");
        }
    },

    checkCurrentUS: function(USname, table) {
        var tableName = table;
        var grSysUpdateSet = new GlideRecord("table");
        grSysUpdateSet.addEncodedQuery("name=" + USname);
        grSysUpdateSet.query();
        return grSysUpdateSet.next();
    },


    checkUS: function(USname, configuration) {
        gs.info("USname " + USname)
        var grSysUpdateSet = new GlideRecord("x_322488_clone_a_0_update_set");
        grSysUpdateSet.addEncodedQuery("configuration=" + configuration.sys_id + "^name=" + USname);
        grSysUpdateSet.query();
        return grSysUpdateSet.next();
    },

    type: 'CloneAnalyseUtils'
};
