sys_script_include.script:
var CloneAnalyseUtils = Class.create();
CloneAnalyseUtils.prototype = {
    initialize: function() {},

    createRecord: function(response, configuration, source) {
        try {
            var i = 0;
            var responseParsed = JSON.parse(response);
            while (i < responseParsed.result.length) {
                var grUpdateSet = new GlideRecord('x_322488_clone_a_0_update_set'); {
                    grUpdateSet.initialize();
                    grUpdateSet.configuration = configuration.sys_id;
                    grUpdateSet.instance = configuration.instance_url;
                    grUpdateSet.name = responseParsed.result[i].name;
                    grUpdateSet.state = responseParsed.result[i].state;
					grUpdateSet.source = source;
                    grUpdateSet.committed_in_the_instance = this.checkCurrentUS(responseParsed.result[i].name, 'sys_update_set');
                    grUpdateSet.retrieved_in_the_instance = this.checkCurrentUS(responseParsed.result[i].name, 'sys_remote_update_set');
					if (grUpdateSet.commited_in_the_instance == false && grUpdateSet.retrieved_in_the_instance == false)
						grUpdateSet.warning = true;
                    grUpdateSet.insert();
                }
                i++;
            }
        } catch (e) {
            gs.info("Clone Analyse: " + JSON.stringify(e), "CloneAnalyseUtils");
        }
    },
    checkCurrentUS: function(USname, table) {
        var tableName = table;
        var grSysUpdateSet = new GlideRecord(tableName);
        grSysUpdateSet.addEncodedQuery("name=" + USname);
        grSysUpdateSet.query();
        return grSysUpdateSet.next();
    },


    type: 'CloneAnalyseUtils'
};
