sys_ui_action.script:
apiCall(current, getCredential(current), '/api/now/v1/table/sys_update_set', 'local');
apiCall(current, getCredential(current), '/api/now/v1/table/sys_remote_update_set', 'Retrieved');
action.setRedirectURL(current);


function apiCall(current, credential, endPoint, source) {
    var request = new sn_ws.RESTMessageV2();
    request.setEndpoint(current.source_instance_url + endPoint);
    request.setHttpMethod('GET');
    request.setBasicAuth(credential.user, credential.password);
    request.setRequestHeader("Accept", "application/json");

    var response = request.execute();
    new x_322488_clone_a_0.CloneAnalyseUtils().createRecord(response.getBody(), current, source);
}

function getCredential(current) {
    var grCredential = new GlideRecord("discovery_credentials");
    if (grCredential.get(current.source_instance_login)) {
        var credential = {
            user: grCredential.user_name,
            password: new global.ScopedEncrypter().decrypt(grCredential.password)
        };
    }

    return credential;
}
